mapper.select:
SELECT id, title, chapterFirstId, initial FROM ( SELECT bs.id, bs.name AS title, MIN(c.id) AS chapterFirstId, bs.initial as initial FROM bookseries bs LEFT JOIN books b ON bs.id = b.series_id LEFT JOIN chapters c ON b.id = c.book_id
bs.initial = #{initial}
GROUP BY bs.id, bs.name, bs.initial UNION ALL SELECT b.id, b.name AS title, MIN(c.id) AS chapterFirstId, b.initial as initial FROM books b LEFT JOIN chapters c ON b.id = c.book_id WHERE b.series_id IS NULL
AND b.initial = #{initial}
GROUP BY b.id, b.name, b.initial UNION ALL SELECT id, name AS title, id AS chapterFirstId, chapters.initial as initial FROM chapters WHERE book_id IS NULL
AND chapters.initial = #{initial}
) t ORDER BY id

select id, name as title, chapters.full_ancient_content as content, chapters.full_modern_content as translation
from chapters
where id = #{chapterId}

select ancient_text as ancientContent, modern_text as modernContent
from paragraphs
where chapter_id = #{chapterId}

SELECT id, name FROM books
WHERE series_id = #{seriesId}
ORDER BY id

SELECT id, name FROM chapters
WHERE book_id = #{bookId}
ORDER BY id

SELECT c.id, CONCAT_WS('-', IFNULL(bs.name, ''), IFNULL(b.name, ''), c.name ) AS title, a.name AS author, a.dynasty, LEFT(c.full_ancient_content, 20) AS description, c.full_ancient_content AS content, c.full_modern_content AS translation FROM chapters c LEFT JOIN books b ON c.book_id = b.id LEFT JOIN bookseries bs ON b.series_id = bs.id LEFT JOIN authors a ON c.author_id = a.id
(c.name LIKE CONCAT('%', #{title}, '%') OR b.name LIKE CONCAT('%', #{title}, '%') OR bs.name LIKE CONCAT('%', #{title}, '%'))
and #{endIndex} >= c.id and c.id >= #{startIndex}
