mapper.select:
SELECT p.id, p.name, p.dynasty_id, p.author_id, p.full_ancient_content, p.full_modern_content, p.links, pt.grade_id, pt.page, ap.name as author, dy.name as dynasty FROM poems p LEFT JOIN authors_poems ap on p.author_id = ap.id LEFT JOIN dynasty dy on p.dynasty_id = dy.id INNER JOIN poem_textbook pt ON pt.poem_id = p.id where pt.grade_id = #{gradeId}
AND p.name LIKE CONCAT('%', #{name}, '%')
ORDER BY p.id ASC

SELECT p.id, p.name, p.dynasty_id, dy.name as dynasty, p.author_id, ap.name AS author, -- 联表获取作者名 p.full_ancient_content, p.full_modern_content FROM poems p INNER JOIN authors_poems ap ON p.author_id = ap.id LEFT JOIN dynasty dy on p.dynasty_id = dy.id WHERE p.id >= #{lastId}
AND p.name LIKE CONCAT('%', #{name}, '%')
ORDER BY p.id ASC LIMIT #{pageSize}

select p.id, p.name, p.dynasty_id, p.author_id, p.full_ancient_content, p.full_modern_content, ap.name AS author, dp.name as dynasty FROM poems p LEFT JOIN authors_poems ap on p.author_id = ap.id LEFT JOIN dynasty dp on p.dynasty_id = dp.id WHERE p.id >= #{lastId}
AND p.full_ancient_content LIKE CONCAT('%',#{keyword},'%')
AND p.dynasty_id = #{dynastyId}
order by p.id LIMIT #{pageSize}

SELECT tmp.dynasty_id as dynasty_id, dy.name AS dynasty, COUNT(dynasty_id) AS count FROM ( SELECT dynasty_id FROM poems
AND full_ancient_content LIKE CONCAT('%', #{keyword}, '%')
) AS tmp LEFT JOIN dynasty dy on tmp.dynasty_id = dy.id GROUP BY tmp.dynasty_id, dy.name ORDER BY count DESC;
